import React, { useEffect } from "react";
import "./App.css";
import { bankOne } from "./banks";

function Pads({note}) {
  useEffect(() => {
    document.addEventListener("keydown", handleKeyPress);
    return () => {
      document.removeEventListener("keydown", handleKeyPress);
    };
  }, []);

  const handleKeyPress = (e) => {
    if (e.keyCode === pad.keyTrigger) {
      console.log(e.keyCode)
    }
  };

  return (
    <div className="display">
      {bankOne.map((pad) => (
        <p note={pad.keyTrigger} className="drum-pad" key={pad.keyTrigger} onClick={handleKeyPress}>
          {pad.keyTrigger}
          <audio
            src={pad.url}
            id={pad.keyTrigger}
            className="clip"
            key={pad.keyTrigger}
          />
        </p>
      ))}
    </div>
  );
}

export default Pads;
