import { useEffect, useState } from "react";
import "./App.css";
import Clavier from "./Clavier";

function App() {
  const [on, setOn] = useState(true);
  const [soundName, setSoundName] = useState("");
  // const [power, setPower] = useState(true)

  const switchBtn = () => {
    return !on ? setOn(true) : setOn(false);
  };

  useEffect(() => {
    const activeSound = document.getElementsByClassName("active");
    if(activeSound){
      setSoundName(activeSound[0].id)
    }
  }, [])


  return (
    <div className="app">
      <h1>Drum-Machine</h1>
      <div id="drum-machine">
        <Clavier on={on} />
        {/* power button */}
        <div>
          Power
          <div className="btn" onClick={switchBtn}>
            <div className={`${on ? "on" : "off"}`}></div>
          </div>
          <h4>{soundName}</h4>
        </div>
        {/* power button */}
      </div>
    </div>
  );
}
export default App;
