import React, { useEffect } from "react";
import "./App.css";
import { bankOne } from "./banks";

function Pads() {

  const handleClick = (e) => {
      console.log(e.id)
    // const tag = document.getElementById(e.id)
  }

  return (
    <div className="display">
      {bankOne.map((pad) => (
        <p className="drum-pad" id={pad.keyTrigger} key={pad.keyTrigger}>
          {pad.keyTrigger}
          <audio
            src={pad.url}
            id={pad.keyTrigger}
            className="clip"
            key={pad.keyTrigger}
          />
        </p>
      ))}
    </div>
  );
}

export default Pads;
