import {useEffect, useState} from "react";
import "./App.css";
import Clavier from "./Clavier";
import { soundName } from "./Touche";

function App() {
  const [on, setOn] = useState(true);
  const [sound, setSound] = useState(soundName)

  const switchBtn = () => {
    return !on ? setOn(true) : setOn(false);
  };

  useEffect(() => {
    setSound(soundName)
  })

  return (
    <div className="app">
      <h1>Drum-Machine</h1>
      <div id="drum-machine">
        <Clavier on={on} />
        {/* power button */}
        <div>
          Power
          <div className="btn" onClick={switchBtn}>
            <div className={`${on ? "on" : "off"}`}></div>
          </div>
          {sound}
        </div>
        {/* power button */}
      </div>
    </div>
  );
}
export default App;
