import { useState, useContext } from "react";
import "./App.css";
import Clavier from "./Clavier";
import { ContextProvider, Context } from "./ContextApi";

function App() {
  const [on, setOn] = useState(true);
  const [bank, setBank] = useState(false)
  const [sound, setSound] = useContext(Context)
  const [volume, setVolume] = useState(0.5)

  const switchBtn = () => {
    return !on ? setOn(true) : setOn(false);
  };

  const switchBank = () => {
    return !bank ? setBank(true) : setBank(false);
  };

  return (
    <div className="app">
      <h1>Drum-Machine</h1>
      <div id="drum-machine">
        <Clavier on={on} volume={volume} />
        {/* power button */}
        <div className="right-side">
          Power
          <div className="btn" onClick={switchBtn}>
            <div className={`${on ? "on" : "off"}`}></div>
          </div>
          {/* power button */}
          {sound}
          <h5>Volume</h5>
          <input
            type="range"
            step="0.01"
            value={volume}
            max="1"
            min="0"
            className="w-50"
            onChange={(e) => setVolume(e.target.value)}
          />
          Bank
          <div className="btn" onClick={switchBtn}>
            <div className={`${on ? "bank1" : "off"}`}></div>
          </div>
        </div>


      </div>
    </div>
  );
}
export default App;
