import React from "react";
import "./App.css";
import { bankOne } from "./banks";

function Pads() {
  const playSound = (e) => {
      console.log()
  }
  return (
    <div className="display">
      {bankOne.map((pad) => (
        <p className="drum-pad" key={pad.keyTrigger} onClick={playSound}>
          {pad.keyTrigger}
          <audio
            src={pad.url}
            id={pad.keyTrigger}
            className="clip"
            key={pad.keyTrigger}
          />
        </p>
      ))}
    </div>
  );
}

export default Pads;
